my $rsa_large_exp_n = '';

my $rsa_large_exp_e = '';

#
#
#
sub rsa_ne_to_rfc2537 {
    my ($hex_n, $hex_e) = @_;

    my $n = Crypt::OpenSSL::Bignum->new_from_hex($hex_n);
    my $e = Crypt::OpenSSL::Bignum->new_from_hex($hex_e);

    # libcrypt-openssl-bignum-perl-0.04 doesn't have num_bytes
    my $e_len = bytes::length($e->to_bin);
    my $pubkey_rr_bin = pack("C n a$e_len a*", 0, $e_len, $e->to_bin,
                             $n->to_bin);
    return '0s' . encode_base64($pubkey_rr_bin, '');
}

our $large_exp_rfc2537 = rsa_ne_to_rfc2537($rsa_large_exp_n, $rsa_large_exp_e);


